import React, { useState, useRef } from "react";
import { Button, Form, Col } from "react-bootstrap";

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

/*
    DeleteProgramme allows a staff member to delete programmes from the system.
*/

function DeleteProgramme({setShow, programmes, refreshTable}) {
    //const ref = useRef();

    /*
        The validated state is used to keep track of the validity of the delete programme form.
        It's initial state is false.
    */    
    const [validated, setValidated] = useState(false);

    /*
        The checkBoxState array is used to keep track of the checked state of the checkboxes.
        It's initial state is false.
    */    
    const [checkBoxState, setCheckBoxState] = useState(new Array(programmes.length).fill(false));

    /*
        notifyAdded is used to display toast notifications for events. It displays a green toast.
    */    
    const notifyAdded = (text) => toast.success(text);

    /*
        notifyNotAdded is used to display toast notifications for events. It displays a red toast.
    */    
        const notifyNotAdded = (text) => toast.error(text);

    /*
        HandleChange gets the data from the form as passes it to the deleteProgramme function.
    */    
        const handleChange = (checkIndex) => {
            const updateCheckboxState = checkBoxState.map((checkbox, arrayIndex) => arrayIndex === checkIndex ? !checkbox: checkbox
            );
            console.log(checkBoxState);
            console.log(updateCheckboxState);

           // setCheckBoxState(updateCheckboxState);
        };

    /*
        HandleSubmit gets the data from the form as passes it to the deleteProgramme function.
    */    
        const handleSubmit = (event) => {


            const form = event.currentTarget;

            if (form.checkValidity() === false) {
              event.preventDefault();
              event.stopPropagation();
            }

            event.preventDefault();
            event.stopPropagation();
            setValidated(true);

            const formData = {
                //name : form.elements.name.value
            }
            //console.log(event)
            //console.log(event.target.checked)


            //deleteProgramme(formData)
        };

        /*
            deleteProgramme creates a post request to the server, which deletes a programme from the system.
        */
        async function deleteProgramme(data) {
            try {
              const res = await fetch("/programmes/delete", {
                method: "POST",
                headers: {
                    token: localStorage.getItem("token"),
                    "Content-type": "application/json",
                },
                body: JSON.stringify(data),
              });

              setShow(false);
              refreshTable();

              const status = await res.statusText;
              if(status=="Unauthorized"){
                notifyNotAdded(data.name + " Already Exists!");
              }
              else{
                notifyAdded(data.name + " Deleted!");
              }

            } catch (err) {
              console.error(err.message);
            }
        }

        return (
            <>
                <Form validated={validated} onSubmit={handleSubmit}>
                    <Form.Group controlId="name">
                        <Form.Label>Choose Programme</Form.Label>
                        {programmes.map((programme, indexNum) => (



                            <Form.Check 
                                label={programme.name}
                                name={programme.name}
                                checked={checkBoxState[indexNum]}
                                onChange={handleChange(indexNum)}


                            />

                        ))}
                        {}
                    </Form.Group>

                    <div class="float-right"><Button type="submit" class="btn btn-custom">Delete</Button></div>
                </Form>

                <ToastContainer 
                    pauseOnHover
                    position="bottom-right"
                />
            </>
        );
}

export default DeleteProgramme;
